function readConfigFile(environment) {
  const configPath = path.join(
    __dirname,
    "..",
    "config",
    `${environment}.json`
  );

  // Adicionar verificação de existência do arquivo
  if (!fs.existsSync(configPath)) {
    console.error(`Arquivo de configuração não encontrado: ${configPath}`);
    // Lançar um erro mais informativo ou retornar um objeto vazio/padrão
    throw new Error(`Configuration file not found for environment: ${environment}`);
  }

  const fileContent = fs.readFileSync(configPath, "utf8");
  const config = JSON.parse(fileContent);
  return config;
}
